	// 查询积分明细列表
	@SuppressWarnings("unchecked")
	@RequestMapping("scoreDetailList")
	@ResponseBody
	public KePuResult orderList(@RequestBody Map<String,String> map,HttpServletRequest request){
		ScoreDetailExample example = new ScoreDetailExample();
		example.setOrderByClause("time desc ");
		ScoreDetailExample.Criteria criteria=example.createCriteria();
		List<ScoreDetail> list = sdmapper.selectByExample(example);
		return KePuResult.ok(ResultConstant.code_ok, "消费明细",list);
	}

